import React, { useEffect, useState } from 'react'

const Timer = () => {
  const modes = {
    work: 1500,
    shortBreak: 300,
    longBreak: 900,
  }
  const [time, setTime] = useState(0)
  const [isRunning, setIsRunning] = useState(false)
  const [activeMode, setActiveMode] = useState('No activated mode')

  const workAct = () => {
    setIsRunning(false)
    setTime(modes.work)
    setActiveMode('work')
  }
  const shortBreakAct = () => {
    setIsRunning(false)
    setTime(modes.work)
    setActiveMode('work')
  }
  const LongBreakAct = () => {
    setIsRunning(false)
    setTime(modes.work)
    setActiveMode('work')
  }

  useEffect(
    () => {
      let interval

      if (isRunning && time > 0) {
        interval = setInterval(() => {
          setTime((prev) => prev - 1)
        }, 1000)
      }
      if (time === 0) {
        setIsRunning(false)
        if (activeMode === 'work') {
          setTime(modes.shortBreak)
          setActiveMode('short break')
        }
        if (activeMode === 'short break') {
          setTime(modes.longBreak)
          setActiveMode('short break')
        }
        if (activeMode === 'long break') {
          setTime(modes.work)
          setActiveMode('long break')
        }
      }
      return () => {
        clearInterval(interval)
      }
    },
    [isRunning],
    [time]
  )

  const formatTime = (time) => {
    const m = Math.floor(time / 60)
    const s = time % 60
    return `${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`
  }

  return (
    <div>
      {formatTime(time)}
      {activeMode}
      <button onClick={() => setIsRunning(true)}>Start</button>
      <button onClick={() => setIsRunning(false)}>Pause</button>
      <button
        onClick={() => {
          setIsRunning(false)
          setTime(0)
          setActiveMode('No activated mode')
        }}
      >
        Reset
      </button>
      <button onClick={() => workAct()}>Work</button>
      <button onClick={() => shortBreakAct()}>Short Break</button>
      <button onClick={() => LongBreakAct()}>Long Break</button>
    </div>
  )
}

export default Timer
