import React from 'react'

const Timer = () => {
  //   const initialTimer = 25 * 60 // 25 хв
  //   const [time, setTime] = useState(initialTimer)
  //   const formatTime = (seconds) => {
  //     const m = seconds*60
  //     const s = seconds%60
  //   }
  let time = 10
  setTimeout(() => {
    console.log(time)
    time--
    if (time == 0) {
      clearTimeout()
    }
  }, 1000)

  return <div>{time}</div>
}

export default Timer
