import React, { useEffect, useState } from 'react'
import styles from './Timer.module.css'

const Timer = () => {
  const modes = {
    work: 1500,
    shortBreak: 300,
    longBreak: 900,
  }

  const [time, setTime] = useState(0)
  const [isRunning, setIsRunning] = useState(false)
  const [activeMode, setActiveMode] = useState('No activated mode')
  const [count, setCount] = useState(0)
  const [countWork, setCountWork] = useState(0)
  const [initialTime, setInitialTime] = useState(modes.work)
  const [customWork, setCustomWork] = useState(modes.work)

  const workAct = () => {
    setIsRunning(false)
    setTime(modes.work)
    setInitialTime(modes.work)
    setActiveMode('work')
  }
  const shortBreakAct = () => {
    setIsRunning(false)
    setTime(modes.shortBreak)
    setInitialTime(modes.shortBreak)
    setActiveMode('short break')
  }
  const longBreakAct = () => {
    setIsRunning(false)
    setTime(modes.longBreak)
    setInitialTime(modes.longBreak)
    setActiveMode('long break')
  }
  const progress = ((initialTime - time) / initialTime) * 100
  useEffect(
    () => {
      let interval

      if (isRunning && time > 0) {
        interval = setInterval(() => {
          setTime((prev) => prev - 1)
        }, 1000)
      }
      if (time === 0) {
        setIsRunning(false)

        if (activeMode === 'work') {
          setCount((prev) => prev + 1)
          setCountWork((prev) => prev + 1)
          if (count === 3) {
            setTime(modes.longBreak)
            setActiveMode('long break')
            setCount(0)
          } else {
            setTime(modes.shortBreak)
            setActiveMode('short break')
          }
          setIsRunning(true)
        } else {
          setTime(modes.work)
          setActiveMode('work')
          setIsRunning(true)
        }
      }
      return () => {
        clearInterval(interval)
      }
    },
    [isRunning],
    [time]
  )

  const formatTime = (time) => {
    const m = Math.floor(time / 60)
    const s = time % 60
    return `${m}:${s < 10 ? '0' : ''}${s}`
    //`${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`
  }

  return (
    <div
      className={`${styles.wrapper} ${
        activeMode === 'work'
          ? styles.work
          : activeMode === 'short break'
          ? styles.short
          : activeMode === 'long break'
          ? styles.long
          : ''
      }`}
    >
      <h1 className={styles.title}>🍅 Pomodoro Timer</h1>

      <div className={`${styles.timer} ${isRunning ? styles.pulse : ''}`}>
        {formatTime(time)}
      </div>

      <p className={styles.mode}>Current: {activeMode}</p>
      <p className={styles.mode}>Progress: {Math.round(progress)}%</p>
      <p className={styles.counter}>Completed Pomodoros: {countWork}</p>
      <input alt="Type your time here" />
      <div className={styles.controls}>
        <button onClick={() => setIsRunning(true)}>▶ Start</button>
        <button onClick={() => setIsRunning(false)}>⏸ Pause</button>
        <button
          onClick={() => {
            setIsRunning(false)
            setTime(0)
            setActiveMode('No activated mode')
            setCountWork(0)
            setCount(0)
          }}
        >
          🔄 Reset
        </button>
      </div>
      <div className={styles.modes}>
        <button onClick={workAct}>💼 Work</button>
        <button onClick={shortBreakAct}>☕ Short Break</button>
        <button onClick={longBreakAct}>😴 Long Break</button>
      </div>
    </div>
  )
}

export default Timer
