import React, { useEffect, useState, useRef } from 'react'
import styles from './Timer.module.css'

const Timer = () => {
  const modes = {
    work: 1500,
    shortBreak: 300,
    longBreak: 900,
  }

  const [time, setTime] = useState(0)
  const [isRunning, setIsRunning] = useState(false)
  const [activeMode, setActiveMode] = useState('No activated mode')
  const [count, setCount] = useState(0)
  const [countWork, setCountWork] = useState(0)
  const [initialTime, setInitialTime] = useState(modes.work)

  const [customWork, setCustomWork] = useState('')
  const [customShortBreak, setCustomShortBreak] = useState('')
  const [customLongBreak, setCustomLongBreak] = useState('')

  const [workTime, setWorkTime] = useState(customWork * 60)
  const [shortTime, setShortTime] = useState(customShortBreak * 60)
  const [longTime, setLongTime] = useState(customLongBreak * 60)

  const setCustomTime = () => {
    setWorkTime(customWork * 60)
    setShortTime(customShortBreak * 60)
    setLongTime(customLongBreak * 60)
  }

  const workAct = () => {
    setIsRunning(false)
    setTime(customWork * 60)
    setInitialTime(customWork * 60)
    setActiveMode('work')
  }
  const shortBreakAct = () => {
    setIsRunning(false)
    setTime(customShortBreak * 60)
    setInitialTime(customShortBreak * 60)
    setActiveMode('short break')
  }
  const longBreakAct = () => {
    setIsRunning(false)
    setTime(customLongBreak * 60)
    setInitialTime(customLongBreak * 60)
    setActiveMode('long break')
  }
  const progress =
    initialTime > 0 ? ((initialTime - time) / initialTime) * 100 : 0

  const beepRef = useRef(null)
  useEffect(() => {
    let interval

    if (isRunning && time > 0) {
      interval = setInterval(() => {
        setTime((prev) => prev - 1)
      }, 1000)
    }
    if (time === 0 && activeMode && activeMode !== 'No activated mode') {
      setIsRunning(false)
      if (beepRef.current) {
        beepRef.current.play()
      }

      if (activeMode === 'work') {
        setCount((prev) => prev + 1)
        setCountWork((prev) => prev + 1)
        if (count === 3) {
          setTime(customLongBreak * 60)
          setActiveMode('long break')
          setCount(0)
        } else {
          setTime(customShortBreak * 60)
          setActiveMode('short break')
        }
        setIsRunning(true)
      } else {
        setTime(customWork * 60)
        setActiveMode('work')
        setIsRunning(true)
      }
    }
    return () => {
      clearInterval(interval)
    }
  }, [time])

  const formatTime = (time) => {
    const m = Math.floor(time / 60)
    const s = time % 60
    return `${m}:${s < 10 ? '0' : ''}${s}`
    //`${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`
  }

  return (
    <div
      className={`${styles.wrapper} ${
        activeMode === 'work'
          ? styles.work
          : activeMode === 'short break'
          ? styles.short
          : activeMode === 'long break'
          ? styles.long
          : ''
      }`}
    >
      <h1 className={styles.title}>üçÖ Pomodoro Timer</h1>
      <div className={`${styles.timer} ${isRunning ? styles.pulse : ''}`}>
        {formatTime(time)}
      </div>
      <p className={styles.mode}>Current: {activeMode}</p>
      <p className={styles.mode}>Progress: {Math.round(progress)}%</p>
      <p className={styles.counter}>Completed Pomodoros: {countWork}</p>
      div className="flex flex-col items-center">
      <svg className="w-40 h-40 transform -rotate-90">
        <circle
          className="text-gray-300"
          stroke="currentColor"
          strokeWidth="10"
          fill="transparent"
          r={radius}
          cx="80"
          cy="80"
        />
        <circle
          className="text-blue-500 transition-all duration-1000"
          stroke="currentColor"
          strokeWidth="10"
          fill="transparent"
          r={radius}
          cx="80"
          cy="80"
          strokeDasharray={circumference}
          strokeDashoffset={offset}
        />
      </svg>
      <div className={styles.settings}>
        <p>‚öôÔ∏è Set custom time (minutes)</p>
        <label>
          Work:
          <input
            type="number"
            value={customWork}
            onChange={(e) => {
              const val = Number(e.target.value)
              setCustomWork(val)
              if (activeMode === 'work') {
                setTime(val * 60)
                setInitialTime(val * 60)
              }
            }}
            className={styles.input}
          />
        </label>
        <label>
          Short Break:
          <input
            type="number"
            value={customShortBreak}
            onChange={(e) => {
              const val = Number(e.target.value)
              setCustomShortBreak(val)
              if (activeMode === 'short break') {
                setTime(val * 60)
                setInitialTime(val * 60)
              }
            }}
            className={styles.input}
          />
        </label>
        <label>
          Long Break:
          <input
            type="number"
            value={customLongBreak}
            onChange={(e) => {
              const val = Number(e.target.value)
              setCustomLongBreak(val)
              if (activeMode === 'long break') {
                setTime(val * 60)
                setInitialTime(val * 60)
              }
            }}
            className={styles.input}
          />
        </label>
        <button onClick={setCustomTime}>Save</button>
      </div>
      <div className={styles.controls}>
        <button onClick={() => setIsRunning(true)}>‚ñ∂ Start</button>
        <button onClick={() => setIsRunning(false)}>‚è∏ Pause</button>
        <button
          onClick={() => {
            setIsRunning(false)
            setTime(0)
            setInitialTime('No progress')
            setActiveMode('No activated mode')
            setCountWork(0)
            setCount(0)
          }}
        >
          üîÑ Reset
        </button>
      </div>
      <div className={styles.modes}>
        <button onClick={workAct}>üíº Work</button>
        <button onClick={shortBreakAct}>‚òï Short Break</button>
        <button onClick={longBreakAct}>üò¥ Long Break</button>
      </div>
      <audio
        ref={beepRef}
        id="beep"
        src="https://actions.google.com/sounds/v1/alarms/beep_short.ogg"
        preload="auto"
      />
    </div>
  )
}

export default Timer
